import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, h2, NavLink } from 'react-router-dom';
import '../App.css';

function PageIndex(){

    const [backendData, setBackendData] = useState([{}]);
    const [fileNames, setFileNames] = useState([]);
    const [fileConts, setFileConts] = useState([]);
    const [fileCurr, setFileCurr] = useState('');
    const [edit, setEdit] = useState(false);


    useEffect(()=>{
        fetch("/api")
        .then(
            response => response.json()
        ).then(
            data => {
                setFileNames(Object.keys(data));
                setFileConts(Object.values(data));
            }
            
        )
    }, []);

    function setPage(e){
        var file = e.target.innerHTML;
        setFileCurr(fileNames[fileNames.indexOf(file)]);
    }

    function enableEdit(){
        setEdit(!edit);
    }


    return (
        <div id="container">
            <div id="pageIndex">

                        {(fileNames.length === 0) ? (
                            <p>Loading</p>
                        ): (
                            fileNames.map((item, i)=>(
                                <div key={i}>
                                    <h5  className="indexLink" onClick={setPage}>{item}</h5>
                                </div>
                            ))
                        )}
            </div>  
            <div id="textPage">
                {edit ? (
                    <>
                    <input value={fileNames[fileNames.indexOf(fileCurr)]} id="fileName" type="text" name="fileName" placeholder="Page title"/><br />

                    <textarea id="fileBody" name="fileBody" placeholder="Input text body here">{fileConts[fileNames.indexOf(fileCurr)]}</textarea><br />
                    <button onClick={enableEdit}>Edit text</button>
                </>
                ) : (
                    <>
                    <h1>
                        {fileNames[fileNames.indexOf(fileCurr)]}
                    </h1>
                    <h2>
                        {fileConts[fileNames.indexOf(fileCurr)]}
                    </h2>
                    <button onClick={enableEdit}>Edit text</button>
                </>
                )}
                
            </div>
        </div>

    )
}

export default PageIndex
