import React, { useEffect, useState } from 'react';
import Form from './components/form.js';
import PageIndex from './components/pageindex.js';
import { BrowserRouter, Routes, Route, Link, NavLink } from 'react-router-dom';
import './App.css';
import Success from './components/success';
import Failure from './components/failure';


function App(){

    const [submitForm, setSubmitForm] = useState();
    const [needReturn, setNeedReturn] = useState();
    
    function showForm(){
        setSubmitForm(!submitForm);
        setNeedReturn(!needReturn);
    }

    return (
        <BrowserRouter>
            <div>
                <header>
                    <nav id="navBar">
                        <h1 id="logo">Piper Info</h1>
                        
                        <div id="links">
                        {needReturn ? (
                            <Link onClick={showForm} to="/" onClick={showForm} className="navLink"> Return</Link>
                        ):(
                            <>
                            <Link className="navLink" to="form" onClick={showForm}>Submit form</Link>
                            <Link className="navLink">Suggest</Link>
                            </>
                        )}
                        </div>

                    </nav>
                </header>


                <Routes>
                    {submitForm ?       <Route path="form" element={<Form />}/>         : 
                         <Route path="/" element={<PageIndex />}/>}
                    
                    <Route path="success" element={<Success />}/>
                    <Route path="failure" element={<Failure />}/>

                </Routes>


                
            </div>
        </BrowserRouter>
    )
}

export default App;
