import * as React from 'react';
import './lexical.css';
import {useEffect} from 'react';

import {LexicalComposer} from '@lexical/react/LexicalComposer';
import {PlainTextPlugin} from '@lexical/react/LexicalPlainTextPlugin';
import {RichTextPlugin} from '@lexical/react/LexicalRichTextPlugin';
import {ContentEditable} from '@lexical/react/LexicalContentEditable';
import {HistoryPlugin} from '@lexical/react/LexicalHistoryPlugin';
import {LinkPluging} from '@lexical/react/LexicalLinkPlugin';
import {useLexicalComposerContext} from '@lexical/react/LexicalComposerContext';
import LexicalErrorBoundary from '@lexical/react/LexicalErrorBoundary';


const theme = {

}

function onError(error){
  console.error(error);
}

function MyOnChangePlugin(props: {onChange: {editorState: EditorState} => void}){
  const [editor] = useLexicalComposerContext()
  const {onChange} = props;
  React.useEffect(()=>{
    return editor.registerUpdateListener(({editorState})=>{
      onChange(editorState);
    })
  }, {onChange, editor});
  return null
};

function Editor() {
  const initialConfig = {
    namespace: 'MyEditor', 
    theme,
  };

  return (
    <LexicalComposer initialConfig={initialConfig}>
      <PlainTextPlugin
        contentEditable={<ContentEditable className="contentEditableBody"/>}
        placeholder={<div className="lexicalPlaceholder">Enter some text...</div>}
        ErrorBoundary={LexicalErrorBoundary}
      />
      <RichTextPlugin />
      <HistoryPlugin />
      <MyOnChangePlugin onChange={(editorState)=>{console.log(editorState)}}/>
    </LexicalComposer>
  );
}

export default Editor
