import React, { useEffect, useState } from 'react';

function Form(){

    useEffect(()=>{
        fetch("http://localhost:5000", {
            method: "POST",
            headers: {
                'Content-type': 'text/html, charset=utf-8'
            }
        })
    });



    return (
        <div>
            <h1>Write new page</h1>
            <h3>Here you can create a new page of info to be viewed. The title you set will be how the page shows on the Index on the left hand side of the main page, and the text content entered into the text area will be the body</h3>

            <form action="http://localhost:5000/" method="post">
                <label for="fileName">Page Title</label><br />
                <input type="text" name="fileName" placeholder="Page title"/><br />
                <label for="fileBody">Info body</label><br />
                <textarea name="fileBody" placeholder="Input text body here"></textarea><br />
                <button>Test</button>
            </form>
        </div>
    )


}

export default Form;
