import React, { useEffect, useState } from 'react';
import '../../node_modules/draft-js/dist/Draft.css';
import EntryPage from './tiptap/EntryPage.js';


function Form(props){

    const [editing, setEditing] = useState(false);

    // useEffect(()=>{
    //     fetch("http://localhost:5000/rich", {
    //         method: "POST",
    //         mode: 'no-cors',
    //         headers: {
    //             'Content-type': 'application/json'
    //         },
    //     })
    // })

    function cancelEdit(){
        setEditing(!editing);
    }





    return (
        <div id="formBody">
            
            
            {props.editing ? (
                <>  
                    <h1>Editing page</h1>
                    <h2>Correct the text below and hit save. Doing so will replace the original page with your ammended one</h2>
                </>
            ) : (
               <> 
                    <h1>Write a new page below</h1>
                    <EntryPage />

                </>
            )}
        </div>
    )


}

export default Form;
