import React, { useEffect, useState } from 'react';
import { Editor, EditorState, RichUtils, convertToRaw, convertFromRaw, convertFromHTML } from 'draft-js';
import {stateToHTML} from 'draft-js-export-html';
import '../App.css';
import '../../node_modules/draft-js/dist/Draft.css';


class DisplayEditor extends React.Component {
    constructor(props){
        super(props);
        
        this.state = {
            editorState: EditorState.createEmpty()
        };
        this.onChange = (editorState) => this.setState({editorState});
        this.currentState = this.props.currentState.bind;
        this.previewText = this.previewText.bind(this);

    }

    previewText(){
        console.log(this.currentState)
    }

    render(){
        return (
            <>
            <button onClick={this.previewText}>Preview</button>
            <div id="displayDiv">
                
                <Editor 
                editorState={this.state.editorState} 
                onChange={this.onChange} 
                readOnly='true'
                />
            </div>
            </>
        )
    };

};

export default DisplayEditor;
