import React, { useEffect, useState } from 'react';


function App(){

    const [backendData, setBackendData] = useState([{}]);
    const [fileNames, setFileNames] = useState([]);
    const [fileConts, setFileConts] = useState([]);

    const example = {
        "user1": "Jim",
        "user2": "Harry",
        "user3": "Bob"
    }

    const strings = ["one", "two", "three"];

    useEffect(()=>{
        fetch("/api")
        .then(
            response => response.json()
        ).then(
            data => {
                setFileNames(Object.keys(data));
                setFileConts(Object.values(data));
            }
            
        )
    }, []);

    function FormSubmit(e){
        e.preventDefault();
        useEffect(()=>{
            fetch("/api", {
                method: "POST",
                headers: {
                    'Content-type': 'text/html, charset=utf-8'
                }
            })
        })
    };



    return (
        <div>
            <div>

                {(fileNames.length === 0) ? (
                    <p>Loading</p>
                ): (
                    fileNames.map((item, i)=>(
                        <div>
                            <h2 key={i}>{item}</h2>
                            <h3>{fileConts[i]}</h3>
                        </div>
                    ))
                )}
            </div>
            <div>
                <form id="newTextForm" action="/" onSubmit={FormSubmit(e)} method="post">
                    <input type="text" name="fileName" placeholder="Enter file name"/> <br />
                    <textarea name="fileBody" placeholder="Type text here" id="" cols="30" rows="10"></textarea>
                    <button type="submit">Submit new file</button>
                </form>
            </div>
        </div>
    )
}

export default App;
