import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, h2, NavLink } from 'react-router-dom';
import '../App.css';

function PageIndex(){

    const [backendData, setBackendData] = useState([{}]);
    const [fileNames, setFileNames] = useState([]);
    const [fileConts, setFileConts] = useState([]);
    const [fileCurr, setFileCurr] = useState();


    useEffect(()=>{
        fetch("/api")
        .then(
            response => response.json()
        ).then(
            data => {
                setFileNames(Object.keys(data));
                setFileConts(Object.values(data));
            }
            
        )
    }, []);

    function setPage(e){
        var file = e.target.innerHTML + '.txt';
        setFileCurr(file);
        console.log(file);
        console.log(fileNames.indexOf(file));
    }


    return (
        <div>
            <div id="pageIndex">

                        {(fileNames.length === 0) ? (
                            <p>Loading</p>
                        ): (
                            fileNames.map((item, i)=>(
                                <div>
                                    <h2 key={i} onClick={setPage}>{item}</h2>
                                    <h3>{fileConts[i]}</h3>
                                </div>
                            ))
                        )}
            </div>  
            <div id="textPage">
                <h2>
                    {fileConts[fileNames.indexOf(fileCurr)]}
                </h2>
            </div>
        </div>

    )
}

export default PageIndex
