import React, { useEffect, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';


function App(){

    const [backendData, setBackendData] = useState([{}]);
    const [fileNames, setFileNames] = useState([{}]);
    const [filesConts, setFileConts] = useState([{}]);

    const example = {
        "user1": "Jim",
        "user2": "Harry",
        "user3": "Bob"
    }

    useEffect(()=>{
        fetch("/api").then(
            response => response.json()
        ).then(
            data => {
                setFileNames(Object.keys(data));
                setFileConts(Object.values(data));

            }
            
        )
    }, [])

    return (
        <div>
            <div>

                {(backendData.length === 0) ? (
                    <p>Loading</p>
                ): (
                    fileNames.map((fileName, i)=>(
                        <div>
                            <h2 key={uuid()}>{fileName}</h2>
                            {/* <h3>{filesConts[i]}</h3> */}
                        </div>
                    ))
                )}
            </div>
            {/* <div>
                <form action="/api" method="post">
                    <input type="text" name="fileName" placeholder="Enter file name"/> <br />
                    <textarea name="fileBody" placeholder="Type text here" id="" cols="30" rows="10"></textarea>
                    <button type="submit">Submit new file</button>
                </form>
            </div> */}
        </div>
    )
}

export default App;
