import React, { useEffect, useState } from 'react';
import uuid from 'react-uuid';


function App(){

    const [backendData, setBackendData] = useState([{}]);

    const example = {
        "user1": "Jim",
        "user2": "Harry",
        "user3": "Bob"
    }

    useEffect(()=>{
        fetch("/api").then(
            response => response.json()
        ).then(
            data => {
                setBackendData(data);
            }
            
        )
    }, [])

    return (
        <div>

            {(backendData.length === 0) ? (
                <p>Loading</p>
            ): (
                backendData.map((item)=>(
                    <div>
                        <h2 key={uuid}>{item.value}</h2>
                    </div>
                    
                ))
            )}

        </div>
    )
}

export default App;
