import React, { useEffect, useState } from 'react';

function Form(props){

    const [editing, setEditing] = useState(false);

    useEffect(()=>{
        fetch("http://localhost:5000", {
            method: "POST",
            headers: {
                'Content-type': 'text/html, charset=utf-8'
            }
        })
    });



    return (
        <div id="formBody">
            {props.editing ? (
                <form action="http://localhost:5000/" method="post">
                    <input defaultValue={props.fileName}></input>
                    <textarea defaultValue={props.fileText}></textarea>
                    <button type='submit'>Save new</button>
                </form>
            ) : (
               <> 
                <h1>Write new page</h1>
                <h3>Here you can create a new page of info to be viewed. The title you set will be how the page shows on the Index on the left hand side of the main page, and the text content entered into the text area will be the body</h3>

                <form action="http://localhost:5000/" method="post">
                    <label htmlFor="fileName">Page Title</label><br />
                    <input id="fileName" type="text" name="fileName" placeholder="Page title"/><br />
                    <label htmlFor="fileBody">Info body</label><br />
                    <textarea id="fileBody" name="fileBody" placeholder="Input text body here"></textarea><br />
                    <button>Test</button>
                </form>
            </>
            )}
        </div>
    )


}

export default Form;
